definition.script:
pipeline {
    agent any
    tools {
        maven 'maven-3'
    }
    stages {
        stage('Checkout & Build') {
            steps {
                script {
                    // Masukkan link repo GitHub kamu lagi
                    git branch: 'main', url: 'https://github.com/muhammadIqbal1210/SOA.git'

                    def services = ['anggotaservice', 'bukuservice', 'peminjamanservice', 'pengembalianservice']

                    for (service in services) {
                        stage("Build ${service}") {
                            dir("${service}") {
                                echo "Sedang memproses ${service}..."
                                // Tambahkan flag -o (offline) jika sudah pernah sukses sebelumnya agar lebih cepat
                                sh 'mvn clean package -DskipTests'
                            }
                        }
                    }
                }
            }
        }
    }
    post {
        success {
            echo 'SEMUA SERVICE BERHASIL DI-BUILD!'
        }
    }
}
