definition.script:
pipeline {
    agent any

    tools {
        // Harus sesuai dengan nama yang kamu buat di Manage Jenkins > Tools
        maven 'maven-3'
    }

    stages {
        stage('Ambil Kode (Git)') {
            steps {
                // Jika kodinganmu ada di lokal folder yang sama dengan Jenkins
                // Jenkins otomatis sudah berada di workspace. 
                // Tapi jika pakai Git, masukkan: git 'https://github.com/user/repo.git'
                echo 'Mengambil kode terbaru...'
            }
        }

        stage('Build Java (Maven)') {
            steps {
                // Menjalankan build tanpa running test agar cepat saat demo
                sh 'mvn clean package -DskipTests'
            }
        }

        stage('Update Container Docker') {
            steps {
                // Perintah ini akan mem-build ulang image dan me-restart container anggota
                // Pastikan nama servicenya sesuai dengan di docker-compose.yml
                sh 'docker-compose up -d --build anggotaservice'
            }
        }
    }

    post {
        success {
            echo 'Selamat! Service Anggota berhasil diperbarui secara otomatis.'
        }
        failure {
            echo 'Build gagal. Silakan cek Console Output untuk melihat errornya.'
        }
    }
}
